package study;

public class postAndPreIncrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=2,j=2,k=2 ,r=2;
		i=i++;//post increment
		j=++j;//pre increment
		k=k--;//post decrement 
		r=--r;//pre decrement
		//System.out.println(i+" "+j+" "+k+" "+r);
		System.out.printf("%d %d %d %d ",i,j,k,r);

	}

}
