package study;

public class shiftOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=25;
		int b=a<<2;//left shift-> 11001
		int c=a>>2;//right shift-> 110
		System.out.println("after left shift b is: " + b + " \nafter right shifht c is: "+c);
		//System.out.println(a>c);
	}

}
